package latihan.java;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// sama seperti for loop tapi tidak ada init statement dan post statement
		var counter = 1;
		
		while(counter < 10) {
			System.out.println("ini angka ke-"+counter);
			counter++;
		}
	}

}
